INSERT INTO Review (game_id, user_id, review_id, review_text) VALUES
(1, 1, 'R1_1', 'Pandemic is a fantastic cooperative game that really makes you work together. The tension builds beautifully as diseases spread.'),
(2, 2, 'R2_2', 'Carcassonne is a classic for a reason. Simple rules but deep strategy with the tile placement.'),
(3, 3, 'R3_3', 'Catan is the game that got me into modern board games. Still play it regularly after all these years.'),
(4, 4, 'R4_4', '7 Wonders is perfect when you want a civilization game that plays quickly. The drafting mechanic is brilliant.'),
(5, 5, 'R5_5', 'Dominion created the deck-building genre and still does it best in my opinion. So many combinations to explore.'),
(6, 1, 'R6_1', 'Ticket to Ride is my go-to game for introducing new players to the hobby. Accessible but still strategic.'),
(7, 2, 'R7_2', 'Codenames is always a hit at parties. Simple concept but leads to lots of laughter and "aha" moments.'),
(8, 3, 'R8_3', 'Terraforming Mars has incredible depth and replayability. The engine-building is so satisfying.'),
(9, 4, 'R9_4', '7 Wonders Duel is one of the best 2-player games out there. Every decision feels important.'),
(10, 5, 'R10_5', 'Agricola is brutally challenging but so rewarding when your farm finally comes together.');